import dataclasses

from pathlib import Path
from typing import Optional, List, Any


@dataclasses.dataclass
class CommandResult:
	'''
	The result of a command having been executed
	'''

	# The return code of the executed command
	returncode: int = 0

	# The stdout generated by the executed command
	stdout: Optional[bytes] = b''

	# The stderr generated by the executed command
	stderr: Optional[bytes] = b''

	# The command that was performed
	command: List[str] = dataclasses.field(default_factory=list)

	# The directory in which the command was executed
	run_dir: Path = Path()

	# Any data that was stored along with the command when it was added to the command_executor
	associated_data: Any = None
